  //  videoElement.controls = true;
  //  videoElement.autoPlay = true;

  //  // Append the video element to the document body or a specific container
  //  document.body.appendChild(videoElement);

   
      };
    
      // Read the Blob data as Base64
      fileReader.readAsDataURL(data);

      // changing blob to text.works
      // videoRef.current.src = URL.createObjectURL(data);
      // const textData = data.text()
      // textData.then((res)=>{
        
      //   console.log(res)
      // })
    };

    socket.onclose = () => {
      console.log("Disconnected from server");
    };

    websocketRef.current = socket;
    console.log({ socket });

    return () => {
      // Clean up the WebSocket connection
      websocketRef.current.close();
    };
  }, []);

  const handlePlayVideo = () => {
    if (canPlay) {
      // Play the video when the user clicks the play button
      videoRef.current.play() .catch(error => {
        console.error("Failed to :", error.message);
      });
    }
  };
  return (
    <div>
    {/* Video element with controls */}
    <video ref={videoRef} controls />

    {/* Play button */}
    <button onClick={handlePlayVideo}>Play Video</button>
  </div>
  );
};

export default VideoPlayer;

