import React, { useEffect, useRef, useState } from "react";

const VideoPlayer = () => {
  const videoRef = useRef(null);
  const websocketRef = useRef(null);
  const [videoURL, setVideoURL] = useState(null);
  const videoChunksRef = useRef([]); // To store received video chunks

  useEffect(() => {
    // Create a WebSocket connection
    const socket = new WebSocket("ws://localhost:8080");

    socket.onopen = () => {
      console.log("Connected to server");
    };

    socket.onmessage = (event) => {
      // Receive video data from the server
      const data = event.data;
      const fileReader = new FileReader();

      // Set up the onload event to handle the result when it's ready
      fileReader.onload = function(event) {
        // The result contains the Base64 encoded data
        const base64Datas = event.target.result;
        const base64Data = base64Datas.replace(/^data:application\/octet-stream;base64,/, '');
        const binaryData = atob(base64Data);

        // Convert the binary data to a Uint8Array buffer
        const arrayBuffer = new Uint8Array(binaryData.length);
        for (let i = 0; i < binaryData.length; i++) {
          arrayBuffer[i] = binaryData.charCodeAt(i);
        }

        // Store the received chunk in the videoChunksRef
        videoChunksRef.current.push(arrayBuffer);

        // Combine all the chunks into a single Uint8Array buffer
        const combinedBuffer = new Uint8Array(videoChunksRef.current.reduce((totalLength, chunk) => totalLength + chunk.length, 0));
        let offset = 0;
        videoChunksRef.current.forEach(chunk => {
          combinedBuffer.set(chunk, offset);
          offset += chunk.length;
        });

        // Create a Blob from the combined Uint8Array buffer
        const videoBlob = new Blob([combinedBuffer], { type: 'video/mp4' });

        // Create an object URL from the Blob
        const videoURL = URL.createObjectURL(videoBlob);

        // Set the video URL state
        setVideoURL(videoURL);
      };

      // Read the Blob data as Base64
      fileReader.readAsDataURL(data);
    };

    socket.onclose = () => {
      console.log("Disconnected from server");
    };

    websocketRef.current = socket;

    return () => {
      // Clean up the WebSocket connection
      websocketRef.current.close();
    };
  }, []);

  const handlePlayVideo = () => {
    if (videoURL) {
      // Play the video if the URL is available
      videoRef.current.src = videoURL;
      videoRef.current.play()
        .catch(error => {
          console.error("Error while playing video:", error);
        });
    }
  };

  return (
    <div>
      {/* Video element with controls */}
      <video ref={videoRef} controls />

      {/* Play button */}
      <button onClick={handlePlayVideo}>Play Video</button>
    </div>
  );
};

export default VideoPlayer;
